import { useState } from "react";
import './components/Color';
import "./App.css";

function App() {
  document.body.style.overflow = "hidden";
  const [count, setCount] = useState(0);
  let ctr = 0;

  function getRandom(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min);
  }

  function renderCircle(xPos, yPos, direction, value, color) {
    const id = (ctr++).toString();
    function handleClick() {
      setCount((prev) => prev + value);
      const el = document.getElementById(id);
      el.style.display = "none";
    }
    return (
      <svg
        className="circle"
        id={id}
        onClick={handleClick}
        style={{
          left: `calc(${xPos}vw - 60px)`,
          top: `calc(${yPos}vw - 60px)`,
          width: 60,
          height: 60,
          position: "absolute",
        }}
      >
        <circle cx={"50%"} cy={"50%"} r={"50%"} fill={color} />
      </svg>
    );
  }
  const colors = [
    new Color("white", 10),
    new Color("green", 25),
    new Color("blue", 50),
    new Color("pink", 75),
    new Color("purple", 100),
    new Color("yellow", 150),
    new Color("red", 300),
  ];

  function renderCircles(buffAmount) {
    const circleBuffer = [];
    for (let i = 0; i < buffAmount; i++) {
      let color = colors[Math.floor(Math.random() * colors.length)];
      let circle = renderCircle(
        getRandom(3, 53),
        getRandom(3, 40),
        getRandom(0, 360),
        color.points,
        color.name
      );
      circleBuffer.push(circle);
    }
    return circleBuffer;
  }
  const [circles] = useState(renderCircles(15));
  return (
    <div className="App">
      <header className="App-header">
        <div className="App-currency">{count}$</div>
        <div className="App-game">{circles}</div>
        <div className="App-upgrades"></div>
      </header>
    </div>
  );
}

export default App;
