import { useState } from 'react';
import './App.css';

function App() {
  document.body.style.overflow = "hidden"
  const [count, setCount] = useState(0);
  let ctr = 0;

  function getRandom(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min);
  }

  function renderCircle(xPos, yPos, direction, value, color) {
    function handleClick(e) {
      setCount((prev) => prev + value);
      e.target.style.visibility = 'hidden';
    }
    return (
      <svg
        className="circle"
        onClick={handleClick}
        style={{
          left: `calc(${xPos}vw - 60px)`,
          top: `calc(${yPos}vw - 60px)`,
          width: 60,
          height: 60,
          position: 'absolute',
        }}
      >
        <circle cx={'50%'} cy={'50%'} r={'50%'} fill={color} />
      </svg>
    );
  }


function renderCircles(buffAmount) {
    const circleBuffer = [];
    for (let i = 0; i < buffAmount; i++) {
       let circle = renderCircle(
          getRandom(3, 53),
          getRandom(3, 40),
          getRandom(0, 360),
          50,
          Object.keys(Color)[Math.floor(Math.random() * Object.keys(Color).length)]
          );
        circleBuffer.push(circle);
    }
    return circleBuffer;
  };
  const [circles] = useState(renderCircles(15));
  return (
    <div className="App">
      <header className="App-header">
      <div className='App-currency'>
        {count}$
      </div>
      <div className='App-game'>   
            {circles}
        </div>
        <div className='App-upgrades'>     
        </div>
      </header>
    </div>
  );
}

export default App;

class Color {
  static Green = new Color('green');
  static Blue = new Color('blue');
  static Yellow = new Color('yellow');
  static Red = new Color('red');
  static Pink = new Color('pink');
  static Orange = new Color('orange');

  constructor(name) {
    this.name = name;
  }
  toString() {
    return `Color.${this.name}`;
  }
}
