import React, { startTransition } from 'react'
import './Game.css';

export default function Game() {
   return (
       <Circle />
   );
}

class Circle extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            xPos: this.getRandom(3, 53),  //starting x-coordinate
            yPos: this.getRandom(3, 40),  //starting y-coordinate
            direction: this.getRandom(0, 360),  //movement direction
            value: 50, //monetary value of the circle, to change later
            color: Object.keys(Color)[Math.floor(Math.random() * Object.keys(Color).length)],  //starting color
        }
    }

    getRandom(min, max) {
        min = Math.ceil(min);
        max = Math.floor(max);
        return Math.floor(Math.random() * (max - min) + min);
    }

    render() {
        console.log(Math.sqrt(Math.pow(500, 2) + Math.pow(500, 2)) * Math.cos(this.state.direction));
        console.log(Math.sqrt(Math.pow(500, 2) + Math.pow(500, 2)) * Math.sin(this.state.direction));
        return (
            <div >
                <svg className='circle' style={{
                    left: `calc(${this.state.xPos}vw - 60px)`, 
                    top: `calc(${this.state.yPos}vw - 60px)`,
                    transform: `translate(${Math.sqrt(Math.pow(500) + Math.pow(500)) * Math.cos(this.state.direction)}px,
                    ${Math.sqrt(Math.pow(500) + Math.pow(500)) * Math.sin(this.state.direction)}px)`,
                    }} 
                    onClick={() => console.log('Hello')} 
                    >
                <circle cx={30} cy={30} r={30} fill={this.state.color}/>
            </svg>
            </div>
               
        );
    }
    
}

class Color {
    static Green = new Color('green');
    static Blue = new Color('blue');
    static Yellow = new Color('yellow');
    static Red = new Color('red');
    static White = new Color('white');
    static Pink = new Color('pink');
    static Orange = new Color('orange');
  
    constructor(name) {
      this.name = name;
    }
    toString() {
      return `Color.${this.name}`;
    }
  }
