import { useState } from 'react';
import './App.css';
import Player from './components/Player';

function App() {
  document.body.style.overflow = "hidden"

  function getRandom(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min);
  }

  const [count, setCount] = useState(0);
  const [visible, setVisible] = useState(true);
  let ctr = 0;
  function renderCircle(xPos, yPos, direction, value, color, id) {
    id++;
    const handleClick = (id) => () => {
      setCount(count + value);

    }
    return (
              <div> 
                {visible ? <svg className='circle' id={id} onClick={handleClick} style={{
                  left: `calc(${xPos}vw - 60px)`, 
                  top: `calc(${yPos}vw - 60px)`,
                  }} 
                  >
              <circle cx={30} cy={30} r={30} fill={color}/>
          </svg> : ''}
          </div>

          );
  }

  function renderCircles(buffAmount) {
    const circleBuffer = [];
    for (let i = 0; i < buffAmount; i++) {
       let circle = renderCircle(
          getRandom(3, 53),
          getRandom(3, 40),
          getRandom(0, 360),
          getRandom(1, 250),
          Object.keys(Color)[Math.floor(Math.random() * Object.keys(Color).length)]
          );
        circleBuffer.push(circle);
    }
    return circleBuffer;
  }
  return (
    <div className="App">
      <header className="App-header">
      <div className='App-currency'>
        {count}$
      </div>
      <div className='App-game'>   
            {renderCircles(20)}
      {/* <Game />    */}
        </div>
        <div className='App-upgrades'>      
        </div>
      </header>
    </div>
  );
}

export default App;

class Color {
  static Green = new Color('green');
  static Blue = new Color('blue');
  static Yellow = new Color('yellow');
  static Red = new Color('red');
  static Pink = new Color('pink');
  static Orange = new Color('orange');

  constructor(name) {
    this.name = name;
  }
  toString() {
    return `Color.${this.name}`;
  }
}
