import { isVisible } from '@testing-library/user-event/dist/utils';
import { useState, useRef } from 'react';
import './App.css';
import Player from './components/Player';

function App() {
  document.body.style.overflow = "hidden"
  const [h, setH] = useState(0);
  const count = useRef(0);
  let ctr = 0;

  function getRandom(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min);
  }

  function renderCircle(xPos, yPos, direction, value, color) {
    ctr++;

    const handleClick = (id) => () => {
      count.current = count.current + value;
      const el = document.getElementById(id);
      el.style.visibility = "hidden";
    }
    return (
                <svg className='circle' id={ctr} onClick={handleClick(ctr)} style={{
                  left: `calc(${xPos}vw - 60px)`, 
                  top: `calc(${yPos}vw - 60px)`,
                  }} 
                  >
              <circle cx={30} cy={30} r={30} fill={color}/>
          </svg>
          );
  }

function renderCircles(buffAmount) {
    const circleBuffer = [];
    for (let i = 0; i < buffAmount; i++) {
       let circle = renderCircle(
          getRandom(3, 53),
          getRandom(3, 40),
          getRandom(0, 360),
          getRandom(1, 250),
          Object.keys(Color)[Math.floor(Math.random() * Object.keys(Color).length)]
          );
        circleBuffer.push(circle);
    }
    return circleBuffer;
  };
  return (
    <div className="App">
      <header className="App-header">
      <div className='App-currency'>
        {count.current}$
      </div>
      <div className='App-game'>   
            {renderCircles(10)}
      {/* <Game />    */}
        </div>
        <div className='App-upgrades'>     
        <button onClick={() => setH(19)}></button> 
        <p>{h}</p>
        </div>
      </header>
    </div>
  );
}

export default App;

class Color {
  static Green = new Color('green');
  static Blue = new Color('blue');
  static Yellow = new Color('yellow');
  static Red = new Color('red');
  static Pink = new Color('pink');
  static Orange = new Color('orange');

  constructor(name) {
    this.name = name;
  }
  toString() {
    return `Color.${this.name}`;
  }
}
