import React from 'react'

export default function Game() {
    const circleContainer = [];
   return (
    <div>
       <Circle />
    </div>
   );
}

function getRandom(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min);
}

class Circle {
    color;
    xPos;
    yPos;
    direction;
    value;

    constructor() {
        this.color = Object.keys(Color)[Math.floor(Math.random() * Object.keys(Color).length)];  //starting color
        this.xPos = this.getRandom(42, 91);  //starting x-coordinate
        this.yPos = getRandom(7, 44);  //starting y-coordinate
        this.direction = getRandom(0, 360);  //movement direction
        this.value = 50; //money value of the circle, to change later
    }

  

    render() {
        return (
            <svg height={50} width={50}>
                <circle cx={25} cy={25} r={25} fill={this.color}/>
            </svg>
        );
    }
    
}

class Color {
    static Green = new Color('Green');
    static Blue = new Color('Blue');
    static Yellow = new Color('Yellow');
    static Red = new Color('Red');
    static White = new Color('White');
    static Pink = new Color('Pink');
    static Orange = new Color('Orange');
  
    constructor(name) {
      this.name = name;
    }
    toString() {
      return `Color.${this.name}`;
    }
  }
