import React, { startTransition } from 'react'

export default function Game() {
   return (
       <Circle />
   );
}

class Circle extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            xPos: this.getRandom(42, 91),  //starting x-coordinate
            yPos: this.getRandom(7, 44),  //starting y-coordinate
            direction: this.getRandom(0, 360),  //movement direction
            value: 50, //monetary value of the circle, to change later
            color: Object.keys(Color)[Math.floor(Math.random() * Object.keys(Color).length)],  //starting color
        }
    }

    getRandom(min, max) {
        min = Math.ceil(min);
        max = Math.floor(max);
        return Math.floor(Math.random() * (max - min) + min);
    }
    

    render() {
        return (
            <svg height={50} width={50} x1={this.state.xPos}>
                <circle cx={25} cy={25} r={25} fill={this.state.color}/>
            </svg>
        );
    }
    
}

class Color {
    static Green = new Color('green');
    static Blue = new Color('blue');
    static Yellow = new Color('yellow');
    static Red = new Color('red');
    static White = new Color('white');
    static Pink = new Color('pink');
    static Orange = new Color('orange');
  
    constructor(name) {
      this.name = name;
    }
    toString() {
      return `Color.${this.name}`;
    }
  }
