    <div className="App">
      <header className="App-header">
      <div className='App-currency'>
        {count}$
      </div>
      <div className='App-game'>   
            {renderCircle(50, 50, 50, 50, "yellow")}
      {/* <Game />    */}
        </div>
        <div className='App-upgrades'>      
        </div>
      </header>
    </div>
  );
}

export default App;
