import { useState } from 'react';
import './App.css';
import Player from './components/Player';

function App() {
  document.body.style.overflow = "hidden"

  function getRandom(min, max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min);
  }

  const [count, setCount] = useState(0);
  function circle(xPos, yPos, direction, value, color) {
    <div >
                <svg className='circle' onClick={() => setCount(count + 1)} style={{
                    left: `calc(50vw - 60px)`, 
                    top: `calc(50vw - 60px)`,
                    }} 
                    >
                <circle cx={30} cy={30} r={30} fill={"yellow"}/>
            </svg>
            </div>
  }
  return (
    <div className="App">
      <header className="App-header">
      <div className='App-currency'>
        {count}$
      </div>
      <div className='App-game'>   
      <div >
                <svg className='circle' onClick={() => setCount(count + 1)} style={{
                    left: `calc(50vw - 60px)`, 
                    top: `calc(50vw - 60px)`,
                    }} 
                    >
                <circle cx={30} cy={30} r={30} fill={"yellow"}/>
            </svg>
            </div>
      {/* <Game />    */}
        </div>
        <div className='App-upgrades'>      
        </div>
      </header>
    </div>
  );
}

export default App;
